(function () {
    "use strict";
  
    function logErrorOrMessage(message) {
      try {
        if (typeof console === "undefined") return;
  
        if ("error" in console) {
          console.error(message);
        } else {
          console.log(message);
        }
      } catch (e) {
        logErrorOrMessage(e);
      }
    }
  
    function createAnchorElementWithHref(href) {
      return (d.innerHTML = '<a href="' + href.replace(/"/g, "&quot;") + '"></a>'), d.childNodes[0].getAttribute("href") || "";
    }
  
    function getHexadecimalSubstring(str, start) {
      return str.substr(start, 2);
    }
  
    function xorDecode(encodedStr, keyIndex) {
      var decodedStr = "";
      var key = getHexadecimalSubstring(encodedStr, keyIndex);
      var i = keyIndex + 2;
  
      for (; i < encodedStr.length; i += 2) {
        var charCode = r(encodedStr, i) ^ key;
        decodedStr += String.fromCharCode(charCode);
      }
  
      try {
        decodedStr = decodeURIComponent(escape(decodedStr));
      } catch (u) {
        logErrorOrMessage(u);
      }
  
      return createAnchorElementWithHref(decodedStr);
    }
  
    function replaceEmailLinks(document) {
      try {
        var emailLinks = document.querySelectorAll("a");
  
        for (var i = 0; i < emailLinks.length; i++) {
          var link = emailLinks[i];
          var cfEmailIndex = link.href.indexOf(emailProtectionMarker);
  
          if (cfEmailIndex > -1) {
            link.href = "mailto:" + xorDecode(link.href, cfEmailIndex + emailProtectionMarker.length);
          }
        }
      } catch (i) {
        logErrorOrMessage(i);
      }
    }
  
    function replaceTemplates(document) {
      try {
        var templates = document.querySelectorAll("template");
  
        for (var i = 0; i < templates.length; i++) {
          replaceEmailLinks(templates[i].content);
        }
      } catch (c) {
        logErrorOrMessage(c);
      }
    }
  
    function replaceObfuscatedContent(document) {
      try {
        replaceEmailLinks(document);
        replaceTemplates(document);
      } catch (r) {
        logErrorOrMessage(r);
      }
    }
  
    var emailProtectionMarker = "/cdn-cgi/l/email-protection#";
    var cfEmailClass = ".__cf_email__";
    var cfEmailAttribute = "data-cfemail";
    var d = document.createElement("div");
  
    replaceObfuscatedContent(document);
  
    (function () {
      var currentScript = document.currentScript || document.scripts[document.scripts.length - 1];
      currentScript.parentNode.removeChild(currentScript);
    })();
  })();
  